import React, { Component } from 'react';
import { Link, withRouter, Redirect } from 'react-router-dom'
import { connect } from 'react-redux'

class QuestionPage extends Component {

    state = {
        value: ''
    }

    handleChange = (event) => {
        this.setState({ value: event.target.value });
    }


    render() {

        const { question, user, authedUser, resultMetrics } = this.props
        const { value } = this.state
        if (question === null) {
            return <p>This Question Doesn't Exist</p>
        }

        const {
            author, timestamp, optionOne, optionTwo, id
        } = question

        const {
            total, optionOneVotes, optionTwoVotes
        } = resultMetrics

        return (
            <div>
                <div><span>{user.name} asks:</span></div>
                <img
                    src={user.avatarURL}
                    alt={`Avatar of ${author}`}
                    className='avatar'
                />
                {/* <div> <span>Would You Rather...</span> </div>

                <div className="radio" onChange={this.handleChange}>
                    <input type="radio" value="one" checked={value === 'one'} /> {optionOne.text}
                    <input type="radio" value="two" checked={value === 'two'} /> {optionTwo.text}
                </div>
                <button disabled={value === ''}>Submit</button> */}
                <div>
                    <h1>Results:</h1>
                    <div>
                        <div>
                            <span>Would you rather {optionOne.text}?</span>
                            <div>{(optionOneVotes / total) * 100}</div>
                            <div>{optionOneVotes} out of {total} votes</div>
                        </div>
                        <div>
                            <span>Would you rather {optionTwo.text}?</span>
                            <div>{(optionTwoVotes / total) * 100}</div>
                           <div><span>{optionTwoVotes} out of {total} votes</span></div>
                        </div>
                    </div>

                </div>
            </div>
        );
    }
}

const mapStateToProps = ({ authedUser, questions, users }, props) => {
    const { id } = props.match.params

    const question = questions[id]
    const user = users[question.author]
    const resultMetrics = {
        total: question.optionOne.votes.length + question.optionTwo.votes.length,
        optionOneVotes: question.optionOne.votes.length,
        optionTwoVotes: question.optionTwo.votes.length,
    }
    return {
        authedUser,
        question: question ? question : null,
        user,
        resultMetrics
    }
}

export default withRouter(connect(mapStateToProps)(QuestionPage));
