import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Link, withRouter, Redirect } from 'react-router-dom'
import Question from './Question'

class QuestionsList extends Component {
    state = {}
    render() {

        if (this.props.authedID === null) {
            return <Redirect to='/' />
        }

        return (
            <div>
                <h3 className='center'>Questions List:</h3>
                <ul className='dashboard-list'>
                    {this.props.authedID}
                    <div>Unanswered Questions:</div>
                    {this.props.unansweredQuestions.map((question) => (
                        <li key={question.id}>
                            <Question id={question.id} />
                        </li>
                    ))}
                    <div>Answered Questions:</div>
                    {this.props.answeredQuestions.map((question) => (
                        <li key={question.id}>
                            <Question id={question.id} />
                        </li>
                    ))}
                </ul>
            </div>
        );
    }
}

const mapStateToProps = ({ authedUser, questions, users }) => {
    const user = users[authedUser]
    const answers = user.answers
    return {
        answeredQuestions: Object.values(questions).filter((question) => {
            for (var key in answers) {
                if (question.id === key) {
                    return true;
                }
            }
            return false;
        }),
        unansweredQuestions: answers.filter((question) => {
            for (var key in questions) {
                if (question.getKey() === key) {
                    return false
                }
            }
            return true
        }),
        authedID: authedUser
    }
}

export default withRouter(connect(mapStateToProps)(QuestionsList))
