import React, { Component } from 'react';
import { Link, withRouter, Redirect } from 'react-router-dom'
import { connect } from 'react-redux'

class QuestionPage extends Component {
    state = {
        value: ''
    }
    render() {

        const { question, user, authedUser } = this.props
        const { value } = this.state
        if (question === null) {
            return <p>This Question Doesn't Exist</p>
        }

        const {
            author, timestamp, optionOne, optionTwo, id
        } = question

        return (
            <div>
                <div><span>{user.name} asks:</span></div>
                <img
                    src={user.avatarURL}
                    alt={`Avatar of ${author}`}
                    className='avatar'
                />
                <div> <span>Would You Rather...</span> </div>
                <div>
                    <input type="radio" value="one" name="one" /> {optionOne.text}
                    <input type="radio" value="two" name="two" /> {optionTwo.text}
                </div>
                <button disabled={value === ''}>Submit</button>
            </div>
        );
    }
}

const mapStateToProps = ({ authedUser, questions, users }, props) => {
    const { id } = props.match.params

    const question = questions[id]
    const user = users[question.author]

    return {
        authedUser,
        question: question ? question : null,
        user
    }
}

export default withRouter(connect(mapStateToProps)(QuestionPage));
